import type Field from './Field.js';
/**
 * Makes the given field nullable, allowing null values for it.
 */
export default class NullableField<I, O> implements Field<I | null, O | null> {
    readonly field: Field<I, O>;
    readonly type: string;
    constructor(field: Field<I, O>);
    validate(value: I | null): I | null;
    serialize(value: I | null): O | null;
    deserialize(value: unknown): I | null;
    encode(value: I | null): string;
    decode(value: string): I | null;
}
